import React, { useState } from 'react';
import Loader from './Loader';
import config from '../config/config';
import state from '../store';

const SubmitButton = ({ isComplete, prompt }) => {

  const [loading, setLoading] = useState(false); //to show user 'sending' or not after they submit

  const handleSubmit = async () => {

    //Ensure all fields of form are filled out before api call
    console.log(isComplete)
    if (isComplete) {
      console.log("form being submitted")
    } else {
      //display alert to user
      alert("Please fill out the form before submitting.")
      return;
    }

    console.log("logging prompt before api call----------------")
    console.log(prompt)
    try {
      console.log("data being fetched........")
      const response = await fetch(config.development.backendUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          prompt,
        })
      })
      console.log("data fetched, now awaiting response.json")
      const data = await response.json();
      console.log("response.json complete")
      state.mealPlan = data.plan //text response to be shown to user

    } catch (error) {
      alert(error)
    }
  }

  return (
    <div>
      {/* Submit form button */}
      <button
        type='submit'
        className='bg-tertiary py-3 px-8 outline-none w-fit text-white font-bold shadow-md shadow-primary rounded-xl'
        onClick={() => handleSubmit(prompt)}
      >
        {loading ? 'Sending...' : 'Submit '}
      </button>
    </div>
  );
};

export default SubmitButton;
